#!/bin/sh
# https://github.com/dxlr8r/kubefs
KUBEFS_DIR=${KUBEFS_DIR:-"$HOME/.local/share/kubefs"}
mkdir -p "$KUBEFS_DIR"
printf '%s\n' '' | base64 --decode > "$KUBEFS_DIR/kubefs.sh"
printf '%s\n' '' | base64 --decode > "$KUBEFS_DIR/kubeauth_init.sh"
printf '%s\n' '' | base64 --decode > "$KUBEFS_DIR/kubefs_addons.sh"
