#!/bin/sh
# https://github.com/dxlr8r/kubefs
BINDIR=${BINDIR:-"$HOME/.local/bin"}
mkdir -p "$BINDIR"
printf '%s\n' '' | base64 --decode > "$BINDIR/kubefs"
chmod +x "$BINDIR/kubefs"
printf '%s\n' '' | base64 --decode > "$BINDIR/kubeauth_init"
chmod +x "$BINDIR/kubeauth_init"
printf '%s\n' '' | base64 --decode > "$BINDIR/kubefs_addons"
chmod +x "$BINDIR/kubefs_addons"
