# kubefs completion                                        -*- shell-script -*-

# This bash completions script was generated by
# completely (https://github.com/dannyben/completely)
# Modifying it manually is not recommended

_kubefs_completions_filter() {
  local words="$1"
  local cur=${COMP_WORDS[COMP_CWORD]}
  local result=()

  if [[ "${cur:0:1}" == "-" ]]; then
    echo "$words"

  else
    for word in $words; do
      [[ "${word:0:1}" != "-" ]] && result+=("$word")
    done

    echo "${result[*]}"

  fi
}

_kubefs_completions() {
  local cur=${COMP_WORDS[COMP_CWORD]}
  local compwords=("${COMP_WORDS[@]:1:$COMP_CWORD-1}")
  local compline="${compwords[*]}"

  case "$compline" in
    'lock session toggle'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "$(find "${KUBEFS_ROOT:-$HOME/.kube}" -type f -name '.kubeconfig' 2>/dev/null)")" -- "$cur")
      ;;

    'lock global toggle'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "$(find "${KUBEFS_ROOT:-$HOME/.kube}" -type f -name '.kubeconfig' 2>/dev/null)")" -- "$cur")
      ;;

    'lock session list'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "")" -- "$cur")
      ;;

    'lock global list'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "")" -- "$cur")
      ;;

    'lock session set'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "$(find "${KUBEFS_ROOT:-$HOME/.kube}" -type f -name '.kubeconfig' 2>/dev/null)")" -- "$cur")
      ;;

    'lock session del'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "")" -- "$cur")
      ;;

    'lock session get'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "")" -- "$cur")
      ;;

    'lock global set'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "$(find "${KUBEFS_ROOT:-$HOME/.kube}" -type f -name '.kubeconfig' 2>/dev/null)")" -- "$cur")
      ;;

    'lock global del'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "")" -- "$cur")
      ;;

    'lock global get'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "")" -- "$cur")
      ;;

    'lock session'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "set toggle del get list")" -- "$cur")
      ;;

    'lock global'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "set toggle del get list")" -- "$cur")
      ;;

    'list-all'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "")" -- "$cur")
      ;;

    'list'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "")" -- "$cur")
      ;;

    'auth'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "$(find "${KUBEFS_ROOT:-$HOME/.kube}" -type f -name '.kubeauth' 2>/dev/null)")" -- "$cur")
      ;;

    'lock'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "global session")" -- "$cur")
      ;;

    'get'*)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "")" -- "$cur")
      ;;

    *)
      while read -r; do COMPREPLY+=("$REPLY"); done < <(compgen -W "$(_kubefs_completions_filter "get list list-all auth lock")" -- "$cur")
      ;;

  esac
} &&
  complete -F _kubefs_completions kubefs

